import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route } from "react-router-dom";
import Header from "./Header";
import io from "socket.io-client";
import TextField from '@material-ui/core/TextField'
import Button from '@material-ui/core/Button'

const socket = io.connect('http://localhost:5000')

function Login(){

  const [message, setMessage] = useState("");

  function handleChange(event){
      const {value} = event.target;
      setMessage(value);
  }

  function handleClick(event){
      socket.emit('message', { 
          message
      });
      event.preventDefault();

      //clear text fields
      setMessage("");
  }

  return (
      <div>
      <Header />
      <form>
            <div className="output"></div>

            <input 
                onChange={handleChange}
                 name="message"
                 type="text"
                 value={message}
                 placeholder="Insert message"
            />
            <button onClick={handleClick}>Send</button>
        </form>
      </div>    
  );
}

function Chat(){

  const [chat, setChat] = useState({username: "", message: ""});
  const [messages, setTextBox] = useState([]);

  function handleChange(event){
      const {name, value} = event.target;
      setChat(prev => {
          if(name === "username"){
              return {
                  username: value,
                  message: prev.message
              }
          }else{
              return {
                  username: prev.username,
                  message: value
              }
          }
      })
  }

  //Listener
  useEffect(function(){
      //reply for logging in
      socket.on('message', (data) => {
        //message received
        const {username, message} = data;
        setTextBox((prev) => {
          return [...prev, {username, message}]
        })
      })

  },[])

  function handleClick(event){

      socket.emit('message', { 
          username: chat.username, 
          message: chat.message 
      });

      setChat({username: chat.username, message: ""});
  }


  function showOutput(message){
    return (
        <div>
          <h3>
            {message.username}: <span>{message.message}</span>
          </h3>
        </div>
    )
  }

  return (
    <div>
        <Header />
        <form>
        <div className="name-field">
        <div className="output">
          {messages.map(showOutput)}
        </div>
        <TextField
            name="username"
            id="outlined-multiline-static"
            variant="outlined"
            label="Username"
            value={chat.username}
            onChange={handleChange}
          />
        </div>
        <div>
          <TextField
            name="message"
            id="outlined-multiline-static"
            variant="outlined"
            label="Message"
            value={chat.message}
            onChange={handleChange}
          />
        </div>
        <Button onClick={handleClick}>Send Message</Button>
      </form>
    </div>
  );
}

export default function App() {

  return (
    <div className="App">
      <Router>
        <Route path="/" exact component={Login}/>
        <Route path="/chat" component={Chat}/>
      </Router>
    </div>
  );
}
