  const [message, setMessage] = useState("");

  function handleChange(event){
      const {value} = event.target;
      setMessage(value);
  }

  function handleClick(event){
      socket.emit('message', { 
          message
      });
      event.preventDefault();

      //clear text fields
      setMessage("");
  }

  return (
      <div>
      <Header />
      <form>
            <div className="output"></div>

            <input 
                onChange={handleChange}
                 name="message"
                 type="text"
                 value={message}
                 placeholder="Insert message"
            />
            <button onClick={handleClick}>Send</button>
        </form>
      </div>    
  );
}
*/
function Chat(){

  const [chat, setChat] = useState({username: "", message: ""});
  const [messages, setTextBox] = useState([]);

  function handleChange(event){
      const {name, value} = event.target;
      setChat(prev => {
          if(name === "username"){
              return {
                  username: value,
                  message: prev.message
              }
          }else{
              return {
                  username: prev.username,
                  message: value
              }
          }
      })
  }

  //Listener
  useEffect(function(){
      //reply for logging in
      socket.on('message', (data) => {
        const {username, message} = data;
        setTextBox((prev) => {
          return [...prev, {username, message}]
        })
      })
      console.log(messages);
  },[])

  function handleClick(event){

      socket.emit('message', { 
          username: chat.username, 
          message: chat.message 
      });

      setChat({username: "", message: ""});
  }

  /*
  function showMessages(){
    return (
      messages.map(function({name,message}){
        <div>
          <h3>
            {name}: <span>{message}</span>
          </h3>
        </div>
      })
    )
  }
  */
  return (
    <div>
        <Header />
        <form>
        <div className="name-field">
        <div className="output"></div>
        <TextField
            name="username"
            id="outlined-multiline-static"
            variant="outlined"
            label="Username"
            value={chat.username}
            onChange={handleChange}
          />
        </div>
        <div>
          <TextField
            name="message"
            id="outlined-multiline-static"
            variant="outlined"
            label="Message"
            value={chat.message}
            onChange={handleChange}
          />
        </div>
        <Button onClick={handleClick}>Send Message</Button>
      </form>
    </div>
  );
}

export default function App() {

  return (
    <div className="App">
      <Router>
        <Route path="/" exact component={Chat}/>
        <Route path="/chat" component={Login}/>
      </Router>
    </div>
  );
}
